users.map:
function (doc, meta) {
  if(doc.doctype && (doc.doctype == "admin" || doc.doctype == "user" || doc.doctype == "prestataire")) {
      emit(meta.id,{name: doc.name,email:doc.email, address : doc.address+' '+doc.zipcode +' '+doc.town, role:doc.role});
 }
}

